import { Component } from "react";
import "./style.css";



class Entrada extends Component {
  
  HandleChange (event){
    let texto = event.target.value;
    console.log(event.target.value);
  }

  HandleSubmit (event){
    console.log(event.value);
  }

  render() {
    return (
      <section className="entrada-container">
        <div className="entrada-wrap">
          <h1 className="entrada-titulo">To Do List</h1>
          <div>
            <input className="entrada-input" type="text" onChange={this.HandleChange.bind(this)}/>
            <button className="entrada-adiciona" type="button"  onClick={this.HandleSubmit.bind(this)}>
              Adicionar
            </button>
          </div>
        </div>
      </section>
    );
  }
}

export default Entrada;
