import React, { Component } from "react";
import "./assets/App.css";
import "./assets/index.css";
import Entrada from "./components/entrada";
import Saida from "./components/saida";

class App extends Component {
  //criar o objeto
  constructor(props) {
    super(props);
    this.state = {
      texto: "",
      lista: [],
      checked:"hidden",
    };

    this.HandleChange = this.HandleChange.bind(this);
    this.HandleSubmit = this.HandleSubmit.bind(this);
    this.HandleChecked = this.HandleChecked.bind(this);
    this.HandleDelete = this.HandleDelete.bind(this);
  }

  //Funções Entrada
  HandleChange(evento) {
    this.setState({
      texto: evento.target.value,
    });
  }

  HandleSubmit(evento) {
      const novoItem = {
        texto: this.state.texto,
      }
      this.setState( prevState =>({
        lista: prevState.lista.concat([novoItem]),
      }));

  }

  //Funções Saida
  HandleChecked(evento) {
    let mostrar = this.state.checked;

    if(mostrar === ""){
      mostrar = "hidden"
    }else{
      mostrar = ""
    }
    console.log(this.state.checked);
    this.setState = ({
      checked: mostrar,
    })
  }

  HandleDelete(evento) {
    console.log(evento.value);
  }

  // Conteudo renderizado na Index.JS 
  render() {
    return (
      <main>
        <Entrada texto={this.state.texto} HandleChange={this.HandleChange} HandleSubmit={this.HandleSubmit} />
        <Saida  lista={this.state.lista} HandleChecked={this.HandleChecked} checked={this.state.checked} HandleDelete={this.HandleDelete} />
      </main>
    );
  }
}

export default App;
