import React, { Component } from "react";
import "./assets/App.css";
import "./assets/index.css";
import Entrada from "./components/entrada";
import Saida from "./components/saida";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      texto: "",
      lista: [],
    };

    this.HandleChange = this.HandleChange.bind(this);
    this.HandleSubmit = this.HandleSubmit.bind(this);
    this.HandleChecked = this.HandleChecked.bind(this);
    this.HandleDelete = this.HandleDelete.bind(this);
  }

  HandleChange(evento) {
    this.setState({
      texto: evento.target.value,
    });
  }

  HandleSubmit(evento) {
    console.log("clicou");
    this.setState( prevState =>({
      texto: prevState.lista.push({}),
    }));
  }

  HandleChecked(evento) {
    console.log(evento.value);
  }

  HandleDelete(evento) {
    console.log(evento.value);
  }

  render() {
    return (
      <main>
        <Entrada texto={this.state.texto} HandleChange={this.HandleChange} HandleSubmit={this.HandleSubmit} />
        <Saida  lista={this.state.lista} HandleChecked={this.HandleChecked} HandleDelete={this.HandleDelete}/>
      </main>
    );
  }
}

export default App;
